mapper.select:
select date
from store 
where brand = #{brand,jdbcType=VARCHAR} and model = #{model,jdbcType=VARCHAR}

mapper.insert:
insert into store (id, brand, model, 
  date, price, count
  )
values (#{id,jdbcType=INTEGER}, #{brand,jdbcType=VARCHAR}, #{model,jdbcType=VARCHAR}, 
  #{date,jdbcType=TIMESTAMP}, #{price,jdbcType=DOUBLE}, #{count,jdbcType=INTEGER}
  )
